package com.queen.core.launch.config;import com.queen.core.launch.props.QueenProperties;import com.queen.core.launch.props.QueenPropertySourcePostProcessor;import org.springframework.boot.context.properties.EnableConfigurationProperties;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.core.Ordered;import org.springframework.core.annotation.Order;/** * @description: QueenPropertyConfiguration * @author: jensen * @date: created by 2020/11/22 2:53 */@Configuration@Order(Ordered.HIGHEST_PRECEDENCE)@EnableConfigurationProperties(QueenProperties.class)public class QueenPropertyConfiguration {    @Bean    public QueenPropertySourcePostProcessor queenPropertySourcePostProcessor() {        return new QueenPropertySourcePostProcessor();    }}