package com.queen.core.launch.server;import com.queen.core.launch.utils.INetUtil;import lombok.Getter;import org.springframework.beans.factory.SmartInitializingSingleton;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.autoconfigure.web.ServerProperties;import org.springframework.context.annotation.Configuration;/** * @description: 服务器消息 * @author: jensen * @date: created by 2020/11/22 3:10 */@Getter@Configurationpublic class ServerInfo implements SmartInitializingSingleton {    private final ServerProperties serverProperties;    private String hostName;    private String ip;    private Integer port;    private String ipWithPort;    @Autowired(required = false)    public ServerInfo(ServerProperties serverProperties) {        this.serverProperties = serverProperties;    }    @Override    public void afterSingletonsInstantiated() {        this.hostName = INetUtil.getHostName();        this.ip = INetUtil.getHostIp();        this.port = serverProperties.getPort();        this.ipWithPort = String.format("%s:%d", ip, port);    }}